import 'package:flutter/material.dart';import 'package:swayam/constants.dart';class BodyText extends StatelessWidget {  final String titleText, bodyText;  BodyText({this.titleText, this.bodyText});  @override  Widget build(BuildContext context) {    Size _size = MediaQuery.of(context).size;    double _maxWidth = _size.width;    double _maxHeight = _size.height;    // print(_maxHeight);    // print(_maxWidth);    return Container(      color: Colors.black87,      child: Column(        children: [          SizedBox(            height: 0.02 * _maxHeight,          ),          SizedBox(            height: 0.02 * _maxHeight,            width: 0.176 * _maxWidth,            child: Divider(              height: 0.02 * _maxHeight,              color: Colors.white,              thickness: _maxHeight / _maxWidth,            ),          ),          Center(            child: Padding(              padding: EdgeInsets.all(_maxWidth / 25),              child: Text(                titleText,                style: kHeadTextStyle,              ),            ),          ),          SizedBox(            height: 0.02 * _maxHeight,          ),          Container(            padding: EdgeInsets.all(_maxWidth / 25),            child: Text(              bodyText,              style: kBodyTextStyle,            ),          ),        ],      ),    );  }}